import React, { PureComponent } from "react";
import './App.css';
import { ApolloClient, InMemoryCache } from '@apollo/client';
import { GET_CATEGORIES, GET_CURRENCIES } from "./data";
import getCategories from "./fetch";
import { render } from "@testing-library/react";
import { responsePathAsArray } from "graphql";
import { toHaveDisplayValue } from "@testing-library/jest-dom/dist/matchers";

const client = new ApolloClient({
  uri: 'http://localhost:4000/',
  cache: new InMemoryCache(),
  onError: ({ networkError, graphQLErrors }) => {
    console.log('graphQLErrors', graphQLErrors)
    console.log('networkError', networkError)
  }
});

class App extends PureComponent {

  constructor(props) {
    super(props);
    this.state = {
      currencies: [],
      categories: []
    };
  }

  getCurrencies = async () => {
    let res = await client.query({
      query: GET_CURRENCIES
    })
    let data = await res;
    let currencies = data.data.currencies
    this.setState({currencies: currencies})

    return currencies;
  }

  getCategories = async () => {
    let res = await client.query({
      query: GET_CATEGORIES
    })
    let data = await res;
    let categories = data.data.categories
    this.setState({categories: categories})

    return categories;
  }

  componentDidMount() {
    this.getCurrencies()
    this.setState({categories: this.getCategories()})

  }  

  render() {
    if(!this.state.currencies[0] && !this.state.categories[0]){
      return <h1>loading...</h1>
    }

    console.log(this.state.categories[0])
    return <ul>{this.state.currencies.map(item => <li key={item.symbol}>{item.label} {this.state.categories.length}</li>)}</ul>;
  }

}

export default App;
