import React, { PureComponent, Suspense } from "react";
import './App.css';
import { ApolloClient, InMemoryCache } from '@apollo/client';
import { GET_CATEGORIES, GET_CURRENCIES } from "./data";
import getCategories from "./fetch";
import { render } from "@testing-library/react";
import { responsePathAsArray } from "graphql";
import { toHaveDisplayValue } from "@testing-library/jest-dom/dist/matchers";

const client = new ApolloClient({
  uri: 'http://localhost:4000/',
  cache: new InMemoryCache(),
  onError: ({ networkError, graphQLErrors }) => {
    console.log('graphQLErrors', graphQLErrors)
    console.log('networkError', networkError)
  }
});

class App extends PureComponent {

  constructor(props) {
    super(props);
    this.state = {
      currencies: [],
      categories: []
    };
  }

  getCurrencies = async () => {
    let res = await client.query({
      query: GET_CURRENCIES
    })
    let data = await res;
    let currencies = data.data.currencies
    this.setState({currencies: currencies})

    return currencies;
  }


  componentDidMount() {
    this.getCurrencies()
    getCategories().then(result => this.setState({categories: result}))
    console.log(this.state)
  }  

  render() {
    return (
      <Suspense fallback={<h1>Loading profile...</h1>}>
        <h1>{this.state.currencies[0].label}</h1>
      </Suspense>
    )

  }

}

export default App;
