import React, { PureComponent, Suspense } from "react";
import { BrowserRouter as Router, Switch, Route, Link } from "react-router-dom";
import { getCategories, getCurrencies } from "./data/getData";
import ProductPage from "./pages/ProductPage";
import CategoryPage from "./pages/CategoryPage";
import { render } from "@testing-library/react";
import { responsePathAsArray } from "graphql";
import { toHaveDisplayValue } from "@testing-library/jest-dom/dist/matchers";

class App extends PureComponent {
  constructor(props) {
    super(props);
    this.state = {
      currencies: [],
      categories: [],
    };
  }

  componentDidMount() {
    getCurrencies().then((result) => this.setState({ currencies: result }));
    getCategories().then((result) => this.setState({ categories: result }));
  }

  render() {
    if (!this.state.categories.length) {
      return <h1>loading...</h1>;
    }

    return (
      <Router>
        <div>
          <nav>
            <ul>
              <li>
                <Link to="/">Home</Link>
              </li>
              <li>
                <Link to="/ProductPage">products</Link>
              </li>
              <li>
                <Link to="/CategoryPage">categories</Link>
              </li>
            </ul>
          </nav>

          {/* A <Switch> looks through its children <Route>s and
              renders the first one that matches the current URL. */}
          <Switch>
            <Route path="/ProductPage">
              <ProductPage />
            </Route>
            <Route path="/CategoryPage">
              <CategoryPage />
            </Route>
          </Switch>
        </div>
      </Router>
    );
  }
}

export default App;
