import React, { PureComponent, Suspense } from "react";
import { BrowserRouter, Routes, Route } from "react-router-dom";
import { getCategories, getCurrencies } from "./data/getData";
import { render } from "@testing-library/react";
import { responsePathAsArray } from "graphql";
import { toHaveDisplayValue } from "@testing-library/jest-dom/dist/matchers";
import ProductPage from "./pages/ProductPage";
import CategoryPage from "./pages/CategoryPage";
import CartPage from "./pages/CartPage";
import NoPage from "./pages/NoPage";
import Layout from "./pages/Layout"

class App extends PureComponent {
  constructor(props) {
    super(props);
    this.state = {
      currencies: [],
      categories: [],
      chosenCategory: "All"
    };
  }

  componentDidMount() {
    getCurrencies().then((result) => this.setState({ currencies: result }));
    getCategories().then((result) => this.setState({ categories: result }));
  }

  displayClothes(){
    this.setState({chosenCategory: "Clothes"})
  }
  displayTech(){
    this.setState({chosenCategory: "Tech"})
  }
  displayAll(){
    this.setState({chosenCategory: "All"})
  }

  handleCategories = [this.displayClothes, this.displayAll, this.displayTech]

  render() {
    if (!this.state.categories.length) {
      return <h1>loading...</h1>;
    }

    return (
      <BrowserRouter>
        <Routes>
          <Route path="/" element={<Layout categories={this.state.categories} handleCategories={this.handleCategories}/>}>
            <Route index element={<CategoryPage category={this.state.chosenCategory}/>} />
            <Route path="products" element={<ProductPage />} />
            <Route path="cart" element={<CartPage />} />
            <Route path="*" element={<NoPage />} />
          </Route>
        </Routes>
      </BrowserRouter>
    );
  }
}

export default App;
